INSERT INTO `page` (`root_id`, `lft`, `rgt`, `level`, `token`, `name`, `title`, `header`, `keywords`, `description`, `content`, `has_menu`, `created_at`, `updated_at`) VALUES (71, 1, 2, 0, 'for_renter', 'Для арендодателей', NULL, 'Для арендодателей', NULL, NULL, '		<div class=\'bLInfo\'>\r\n			<h2 class=\'bLInfo__eH2\'>Добропорядочные и платежеспособные арендаторы ищут жилье! Помещения – предлагать!</h2>\r\n\r\n			<p class=\'bLInfo__eP\'>\r\n				Вы являетесь счастливым обладателем помещения и не знаете, с кем поделиться радостью и что вообще с этим делать? Вы хотите его сдать, но не знаете кому?\r\n			</p> \r\n\r\n			<p class=\'bLInfo__eP\'>\r\n				Enter знает, как вам помочь! Мы ищем помещения в аренду для открытия новых магазинов нашей сети!\r\n			</p>\r\n\r\n			<h2 class=\'bLInfo__eH2\'>Наши пожелания предельно просты</h2>\r\n			<p class=\'bLInfo__eP\'>\r\n				Площадь: 150 – 400 кв. м.<br />\r\n				Формат: street retail<br />\r\n				Этаж: первый (также рассматриваем двухуровневые помещения)<br />\r\n				Расположение: торговые улицы, зоны с высоким пешеходным и автомобильным траффиком<br />\r\n			</p>\r\n\r\n\r\n			<p class=\'bLInfo__eP\'>\r\n				Мы ждем ваших предложений (легкая сговорчивость будет вашим плюсом), со своей стороны гарантируем честность, законопослушность (чтим Гражданский Кодекс, ДА!), высокий профессионализм и исправную платежеспособность.\r\n			</p>\r\n\r\n			<h2 class=\'bLInfo__eH2\'>Можно и нужно связываться с нами любым удобным способом</h2>\r\n			<p class=\'bLInfo__eP\'>\r\n				Написать нам: <a href="mailto:arenda@enter.ru" class="underline">arenDA@enter.ru</a><br />\r\n				Или позвонить: +7(495) 775-91-75 (доб. 1356)<br />\r\n				В конце концов, вы можете просто прийти к нам в гости в наш офис! Гостеприимная встреча и кофе с вкусными плюшками вам гарантированы!<br />\r\n			</p>\r\n		</div>', 1, '2012-02-16 13:26:55', '2012-02-16 13:26:59');
