UPDATE `page` SET `content` = '<div class=\'bAbout\'>\n\n  <!-- txt block -->\n\n  <div class=\'bAbout__eBlock\'>\n<div id="fb-root"></div>\n<script>(function(d, s, id) {\nvar js, fjs = d.getElementsByTagName(s)[0];\nif (d.getElementById(id)) return;\njs = d.createElement(s); js.id = id;\njs.src = "//connect.facebook.net/ru_RU/all.js#xfbml=1";\nfjs.parentNode.insertBefore(js, fjs);\n}(document, \'script\', \'facebook-jssdk\'));</script>\n<div style="float:right; margin-bottom:20px" class="fb-like-box" data-href="http://www.facebook.com/enter.ru" data-width="292" data-show-faces="true" data-stream="true" data-header="true"></div>\n\n    <h2 class=\'bAbout__eTitle\'>Что такое Enter</h2><br />\n\n    <p class=\'bAbout__eText\'>\n      Enter – это новый способ покупать<br /><br />\n    </p>\n      <ul class=\'bAbout__eList\'>\n        <li>•  Большинство непродовольственных товаров в одном месте.</li>\n        <li>•  Возможность купить, оплатить и получить товар любым удобным способом.</li>\n        <li>•  Гарантия безопасной покупки.</li>\n        <li>•  Качественный сервис до, во время и после продажи.</li>\n      </ul><br />\n    <p class=\'bAbout__eText\'>\n      Enter – это 20 000 товаров: мебель, бытовая техника, электроника, товары для детей, товары для дома,\n      для сада и ремонта, спорттовары, ювелирные украшения, часы – которые мы волшебным образом\n      уместили в небольших удобных магазинах, на сайте, в каталогах, мобильных приложениях и социальных\n      сетях.<br /><br />\n\n      Мы делаем покупки безопасными, давая возможность оплатить и забрать товар удобным способом.<br /><br />\n\n      Мы даем гарантии, что привезем вовремя, соберем мебель, подключим и настроим технику, установим\n      тренажер, научим, как работать на PC. Для этого у нас есть собственная сервисная <b>служба F1.</b><br /><br />\n\n      <b>Мы дарим время для настоящего. Честно. С любовью. Как для себя.</b><br /><br />\n    </p>\n  </div>\n  <!-- txt block -->\n\n</div>' WHERE `id` = 57;
