  public function getIdByToken($token)
  {
    $q = $this->createQuery()
      ->select('id')
    ;

    if (false !== strpos($token, '/'))
    {
      list($tokenPrefix, $token) = explode('/', $token);
      $q->where('token_prefix = ? AND token = ?', array($tokenPrefix, $token));
    }
    else {
      $q->where('token = ?', $token);
    }

    $q->setHydrationMode(Doctrine_Core::HYDRATE_SINGLE_SCALAR);

    return $q->fetchOne();
  }

