class ProductProperty extends BaseProductProperty
{
  public function importFromCore(array $data)
  {
    parent::importFromCore($data);

    if (isset($data['type_id'])) switch ($data['type_id'])
    {
      case 1:
        $this->type = 'boolean';
        break;
      case 2:
        $this->type = 'date';
        break;
      case 3:
        $this->type = 'integer';
        break;
      case 4:
        $this->type = 'string';
        break;
      case 5:
        $this->type = 'select';
        break;
    }
  }
}
