class ProductPropertyOption extends BaseProductPropertyOption
{
  public function importFromCore(array $data)
  {
    parent::importFromCore($data);

    $this->property_id = !empty($data['property'][0]['id']) ? ProductPropertyTable::getInstance()->getIdByCoreId($data['property'][0]['id']) : null;
    
    if (true === $this->value)
    {
      $this->value = 'да';
    }
    else if (false === $this->value)
    {
      $this->value = 'нет';
    }
  }
}
