class ProductPropertyRelationTable extends myDoctrineTable
{

  /**
   * Returns an instance of this class.
   *
   * @return object ProductPropertyRelationTable
   */
  public static function getInstance()
  {
    return Doctrine_Core::getTable('ProductPropertyRelation');
  }

  public function getValueFieldByType($type)
  {
    $field = null;

    switch ($type)
    {
      case 'select':
        $field = 'option_id';
        break;
      case 'string':
        $field = 'value_string';
        break;
      case 'integer':
        //$field = 'value_integer';
        $field = 'value_float';
        break;
      case 'float':
        $field = 'value_float';
        break;
      case 'text':
        $field = 'value_text';
        break;
      case 'boolean':
        $field = 'value_boolean';
        break;
      default:
        $field = 'value';
        break;
    }

    return $field;
  }

  public function getRealValue($productPropertyRelation)
  {
    $property = ProductPropertyTable::getInstance()->getById($productPropertyRelation['property_id'], array('with_options' => false, 'hydrate_array' => true));
    $field = $this->getValueFieldByType($property['type']);

    return $productPropertyRelation[$field];
  }

  public function countBooleanValueByProperty($property_id, $category_id)
  {
      $return = ProductPropertyRelationTable::getInstance()->createQuery('productPropertyRelation')
        ->select('SUM(IF(value_boolean = 1, 1, 0)) AS sum_true, SUM(IF(value_boolean = 0, 1, 0)) as sum_false')
        ->innerJoin('productPropertyRelation.Product product WITH product.view_list = true')
        ->innerJoin('product.CategoryRelation productCategoryProductRelation')
        ->where('productPropertyRelation.property_id = ?', $property_id)
        ->andWhere('productCategoryProductRelation.product_category_id = ?', $category_id)
        ->setHydrationMode(Doctrine_Core::HYDRATE_SCALAR)
        ->execute()
      ;

      return isset($return[0]) ? array_values($return[0]) : array(0, 0);
  }

  public function getCacheEraserKeys($record, $action = null)
  {
    $return = array();


    $q = ProductTable::getInstance()->createQuery('product')
      ->select('product.core_id')
      ->innerJoin('product.PropertyRelation productPropertyRelation')
      ->where('productPropertyRelation.id = ?', $record->id)
      ->setHydrationMode(Doctrine_Core::HYDRATE_SINGLE_SCALAR)
    ;

    $ids = $q->execute();
    if (!is_array($ids))
    {
      $ids = array($ids);
    }

    foreach ($ids as $id)
    {
      $return[] = "product-{$id}-";
    }

    return $return;
  }
}
