<?php

/**
 * ShopTable
 *
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class ShopTable extends myDoctrineTable
{

  /**
   * Returns an instance of this class.
   *
   * @return object ShopTable
   */
  public static function getInstance()
  {
    return Doctrine_Core::getTable('Shop');
  }

  public function getForRoute(array $params)
  {
    $id = isset($params['shop']) ? $this->getIdBy('token', $params['shop']) : null;

    return $this->getById($id);
  }

  public function getListByProduct($product_id, array $params = array())
  {
    $this->applyDefaultParameters($params);

    $q = $this->createBaseQuery($params);

    $q->leftJoin('shop.Stock stock')
      ->leftJoin('stock.ProductRelation stockProductRelation')
      ->addWhere('stockProductRelation.product_id = ?', $product_id)
    ;

    $q->select('shop.token, shop.name, shop.region_id, shop.address, SUM(stockProductRelation.quantity) AS quantity')
      ->groupBy('shop.token, shop.name, shop.region_id, shop.address')
    ;

    $this->setQueryParameters($q, $params);

    return $q->execute();
  }

  public function getListByRegion($region_id, array $params = array())
  {
    $this->applyDefaultParameters($params);

    $q = $this->createBaseQuery($params);

    $q->addWhere('shop.region_id = ?', $region_id);

    $this->setQueryParameters($q, $params);

    return $q->execute();
  }
}