<?php

/**
 * StockTable
 *
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class StockTable extends myDoctrineTable
{

  /**
   * Returns an instance of this class.
   *
   * @return object StockTable
   */
  public static function getInstance()
  {
    return Doctrine_Core::getTable('Stock');
  }

  public function getListByProduct($product_id, array $params = array())
  {
    $this->applyDefaultParameters($params);

    $q = $this->createBaseQuery($params);

    $q->leftJoin('stock.ProductRelation stockProductRelation')
      ->addWhere('stockProductRelation.product_id = ?', $product_id)
    ;

    $q->select('stock.token, stock.name, stock.region_id, stock.shop_id, SUM(stockProductRelation.quantity) AS quantity')
      ->groupBy('stock.token, stock.name, stock.region_id, stock.shop_id')
    ;

    $this->setQueryParameters($q, $params);

    return $q->execute();
  }
}