class UserTag extends BaseUserTag
{
  public function exportToCore()
  {
    $products = $this->getProductRelation();
    foreach ($products as &$product)
    {
      $product = $product;
    }
    $data = array(
      'name'      => $this->name,
      'user_id'   => $this->user_id,
      'position'  => $this->position,
      'products'  => $products,
    );
    foreach ($data as $key => $value)
    {
      if (empty($value))
      {
        unset($data[$key]);
      }
    }
    return $data;
  }
}
